/* Reference: https://github.com/onokumus/metisMenu */
(function() {
    // MetisMenu initialization placeholder
    window.MetisMenu = function(el) {
        this.el = el;
        this.init();
    };

    MetisMenu.prototype.init = function() {
        const items = this.el.querySelectorAll('.has-arrow');
        items.forEach(item => {
            item.addEventListener('click', function(e) {
                e.preventDefault();
                const submenu = this.nextElementSibling;
                if (submenu && submenu.classList.contains('sub-menu')) {
                    submenu.classList.toggle('active');
                    submenu.setAttribute('aria-expanded', submenu.classList.contains('active'));
                }
            });
        });
    };
})();
